mapper.select:
select

    VERSION,
    FRAME,
    NAME,
    REMARK,
    URL,
    TAKE_PART_IN,

from a_about


        and VERSION = #{version}


        and FRAME = #{frame}


        and NAME = #{name}


        and REMARK = #{remark}


        and URL = #{url}


        and TAKE_PART_IN = #{takePartIn}
